<script>
import { mapState } from 'vuex'

export default {
  name: 'Sidebar',
  data () {
    return {
      keyDropdown: {
        open_input: false,
        open_galery: false,
        open_category: false,
        open_contact: false,
        open_story: false
      }
    }
  },
  computed: {
    ...mapState(['sideBarOpen'])
  },
  methods: {
    clearData () {
      this.$store.commit('fetchImage', { title: '', description: '', image_url: '' })
    },
    changeDropdown (props) {
      // console.log(props)
      // console.log(!this[props])
      this[props] = !this[props]
      // this[props === 'open_input' ? 'open_galery' : 'open_input'] = !this[props === 'open_input' ? 'open_galery' : 'open_input']
    },
    handleDropdown (type) {
      const keyDd = Object.keys(this.keyDropdown)
      for (const key of keyDd) {
        if (key !== type) {
          this.keyDropdown[key] = false
        }
      }
      this.keyDropdown[type] = !this.keyDropdown[type]
    }
  }
}
</script>
